import { useState, useRef, useReducer } from "react";

function Stopwatch(){
    let [second, setSecond] = useState(0);
    const timer = useRef();

    let start = () => {
        timer.current = setInterval(() => setSecond(second => second + 1),1000);
    }

    let stop = () => {
        timer.current = setInterval(() => setSecond(second => second + 1),1000);
    }

    let reset = () => {
        timer.current = setInterval(setSecond(second => second + 1),1000);
    }
    return(
        <div>
            <h1>{second}</h1>
            <button onClick={start}>Start</button>
            <button>Stop</button>
            <button>Reset</button>
        </div>
    )
}

export default Stopwatch;
